import React, { useState } from "react";

const styles = {
  right: {
    backgroundColor: "white",
    height: "100vh",
    width: "50%",
  },
  input: {
    border: "none",
    borderBottom: "1px solid grey",
    outline: "none",
    display: "block",
    marginTop: "30px",
    minWidth: "90%",
    fontFamily: "Arial",
  },
  textarea: {
    minWidth: "10%",
    outline: "none",
    display: "block",
    marginTop: "30px",
    maxWidth: "90%",
    minWidth: "90%",
    minHeight: "50px",
    fontFamily: "Arial",
    border: "1px solid grey",
  },
  form: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  button: {
    marginTop: "10px",
    width: "15%",
    border: "1px solid grey",
    backgroundColor: "white",
    color: "grey",
  },
};

function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [nameStyle, setNameStyle] = useState({});
  const [messageStyle, setMessageStyle] = useState({});
  const [emailStyle, setEmailStyle] = useState({});

  const handleInputChange = (e) => {
    const { target } = e;
    const inputType = target.name;
    const inputValue = target.value;

    if (inputType === "email") {
      setEmail(inputValue);
    } else if (inputType === "name") {
      setName(inputValue);
    } else if (inputType === "message") {
      setMessage(inputValue);
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!email || !name || !message) {
      const errorStyle = { color: "red", fontWeight: "bold" };
      setNameStyle(name ? {} : errorStyle);
      setMessageStyle(message ? {} : errorStyle);
      setEmailStyle(email ? {} : errorStyle);
      return;
    } else {
      alert(`Email sent`);
      setName("");
      setMessage("");
      setEmail("");
    }
  };

  return (
    <div style={styles.right}>
      <form style={styles.form}>
        <input
          style={styles.input}
          name="name"
          type="text"
          onChange={handleInputChange}
          placeholder="Name"
          value={name}
          style={nameStyle}
        />
        <input
          style={styles.input}
          name="email"
          type="email"
          onChange={handleInputChange}
          placeholder="Email"
          value={email}
          style={emailStyle}
        />
        <textarea
          style={{ ...styles.textarea, fontFamily: "Arial" }}
          name="message"
          type="text"
          onChange={handleInputChange}
          placeholder="Message"
          value={message}
          style={messageStyle}
        />
        <button
          style={styles.input}
          name="email"
          type="email"
          onChange={handleFormSubmit}
          placeholder="Email"
          value={email}
        >
          Send
        </button>
      </form>
    </div>
  );
}

export default Contact;
