import React from "react";
import { Document, Page, pdfjs } from "react-pdf";
import fileDownload from "js-file-download"; // You need to install this library separately

pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
import pdf from "../pdf/mypdf.pdf";

const styles = {
  container: {
    marginLeft: "30px",
    marginTop: "30px",
  },
  p: {
    display: "block",
    marginBottom: "10px",
    color: "black",
    fontSize: "20px",
  },
};

function MyDocument() {
  return (
    <Document>
      <Page>
        <p>This is my PDF document!</p>
      </Page>
    </Document>
  );
}

function Resume() {
  const downloadPDF = () => {
    const pdfBlob = new Blob([<MyDocument />], { type: "application/pdf" });
    fileDownload(pdfBlob, "my_document.pdf");
  };

  return <button onClick={downloadPDF}>Download PDF</button>;
}

export default Resume;
